import React, {Component} from 'react'
import './style.css'
import Top from './Top'
import List from './List'
import Bom from './Bom'

class Todo extends Component{
  constructor(props){
    super(props)
    this.state = {
      list: [
        {
          status: 0,
          context: '啊时间的话噶几红烧冬瓜',
        },
        {
          status: 1,
          context: '啊时间的话噶几红烧冬瓜',
        },
      ]
    }
  }
  toggleAll = ()=>{
    const list = JSON.parse(JSON.stringify(this.state.list))
    list.forEach(el=>{
      el.status = 1
    })
    this.setState({
      list
    })
  }
  toggleComplated = (index)=>{
    const list = JSON.parse(JSON.stringify(this.state.list))
    list[index].status = list[index].status===0? 1:0
    this.setState({
      list
    })
  }
  render(){
    return (
      <div className='todo'>
        <h1>TODO-REACT</h1>
        <div className="con">
          <Top list={this.state.list} toggleAll={this.toggleAll}></Top>
          <List list={this.state.list} toggleComplated={this.toggleComplated}></List>
          <Bom></Bom>
        </div>
      </div>
    )
  }
}

export default Todo
