import React, {Component} from 'react'
import './style.css'

class Top extends Component{
  constructor(props){
    super(props)
  }
  getStatus = ()=>{
    let flag = false;
    let statusArr= []
    this.props.list.forEach((el)=>{
      statusArr.push(el.status)
    })
    flag = !statusArr.includes(0)
    return flag? 'complated': 'quan'
  }
  toggleAll= ()=>{
    this.props.toggleAll()
  }
  render(){
    return (
      <div className="top">
        <i className={this.getStatus()} onClick={this.toggleAll()}></i>
        <div className="input">
          <input type="text" placeholder='todo something'/>
        </div>
      </div>
    )
  }
}

export default Top
