import React, {Component} from 'react'
import './style.css'

class Bom extends Component{
  getLeft = ()=>{
    let count = 0
    this.props.list.forEach(el=>{
      if(el.status === 0){
        count++
      }
    })
    return count === 1? '1 item left': `${count} items left`
  }
  render(){
    return (
      <div className="bom">
        <div className="left">{this.getLeft}</div>
        <div className="center">
          <span>All</span>
          <span>Active</span>
          <span>Complated</span>
        </div>
        <div className="right">Clear Complated</div>
      </div>
    )
  }
}

export default Bom
